import { useEffect, useState } from "react";

function AllOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadOrders();
  }, []);

  const loadOrders = async () => {
    try {
      const res = await fetch("/orders"); // ðŸ”¥ SADECE BURASI KRÄ°TÄ°K
      if (!res.ok) throw new Error("Failed");

      const data = await res.json();
      setOrders(data);
    } catch (err) {
      console.error(err);
      alert("Orders could not be loaded");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>All Orders</h2>

      {loading && <p>Loading...</p>}

      {!loading && orders.length === 0 && (
        <p>No orders found.</p>
      )}

      {!loading && orders.length > 0 && (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>ID</th>
              <th>Status</th>
              <th>Price</th>
              <th>Date</th>
              <th>Time</th>
              <th>Deliverer</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AllOrders;
