import { useState } from "react";
import { getOrderById } from "../../api/orderApi";

function AllOrders() {
  const [orderIds, setOrderIds] = useState("");
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadOrders = async () => {
    setLoading(true);
    setOrders([]);

    const ids = orderIds
      .split(",")
      .map(id => id.trim())
      .filter(Boolean);

    const results = [];

    for (const id of ids) {
      const order = await getOrderById(id);
      if (order) results.push(order);
    }

    setOrders(results);
    setLoading(false);
  };

  return (
    <div>
      <h2>All Orders</h2>

      <input
        placeholder="Order IDs (1,2,3)"
        value={orderIds}
        onChange={e => setOrderIds(e.target.value)}
      />

      <br /><br />
      <button onClick={loadOrders}>Load</button>

      {loading && <p>Loading...</p>}

      {orders.length > 0 && (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>ID</th>
              <th>Status</th>
              <th>Price</th>
              <th>Date</th>
              <th>Time</th>
              <th>Deliverer</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {!loading && orders.length === 0 && <p>No orders loaded.</p>}
    </div>
  );
}

export default AllOrders;
