import { useState } from "react";
import { requestAvailability, requestTimeOff } from "../../api/peopleApi";

function AvailabilityRequest() {
  const [form, setForm] = useState({
    userId: "",
    date: "",
    start: "",
    end: ""
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  return (
    <div>
      <h2>Availability / Time Off</h2>

      <input name="userId" placeholder="User ID" onChange={handleChange} />
      <input type="date" name="date" onChange={handleChange} />
      <input type="time" name="start" onChange={handleChange} />
      <input type="time" name="end" onChange={handleChange} />

      <br /><br />

      <button onClick={() => requestAvailability(form)}>
        Availability Request
      </button>

      <button onClick={() => requestTimeOff(form)}>
        Time Off Request
      </button>
    </div>
  );
}

export default AvailabilityRequest;
