import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../auth/AuthContext";

function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  if (!user) return null;

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav
      style={{
        padding: 12,
        borderBottom: "1px solid #ccc",
        display: "flex",
        alignItems: "center",
        gap: 10
      }}
    >
      <strong>
        {user.fullName} ({user.role})
      </strong>

      <span>|</span>

      {/* MANAGER NAVIGATION */}
{user.role === "MANAGER" && (
  <>
    <Link to="/people">People</Link>{" | "}
    <Link to="/people/add">Add User</Link>{" | "}
    <Link to="/people/promote">Promote</Link>{" | "}
    <Link to="/people/availability">Availability Request</Link>{" | "}
    <Link to="/people/availability/requests">Approve Requests</Link>{" | "}
    <Link to="/people/schedule">Schedule</Link>{" | "}
  </>
)}


      {/* SHARED */}
      <Link to="/usage/list">Usage</Link>

      <span style={{ marginLeft: "auto" }}>
        <button onClick={handleLogout}>Logout</button>
      </span>
    </nav>
  );
}

export default Navbar;
