import { useState } from "react";
import { getScheduleByUserId } from "../../api/peopleApi";

const DAYS = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];

function ScheduleView() {
  const [userId, setUserId] = useState("");
  const [slots, setSlots] = useState([]);

  const load = async () => {
    console.log("游니 Fetching schedule for userId:", userId);
    const data = await getScheduleByUserId(userId);
    console.log("游닌 Response:", data);
    setSlots(data);
  };

  return (
    <div>
      <h2>Employee Schedule</h2>

      <input
        placeholder="User ID"
        value={userId}
        onChange={e => setUserId(e.target.value)}
      />
      <button onClick={load}>Load</button>

      <table border="1" width="100%">
        <thead>
          <tr>
            <th>Day</th><th>Start</th><th>End</th>
          </tr>
        </thead>
        <tbody>
          {slots.map((s,i)=>(
            <tr key={i}>
              <td>{DAYS[s.dayOfWeek]}</td>
              <td>{s.startTime}</td>
              <td>{s.endTime}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ScheduleView;
