import Navbar from "./Navbar";
import StaffNavbar from "./StaffNavbar";
import { Outlet } from "react-router-dom";
import { useAuth } from "../pages/auth/AuthContext"; // ‚úÖ DOƒûRU PATH

function MainLayout() {
  const auth = useAuth();

  // üîê Login sayfasƒ±nda user null olur ‚Üí navbar basma
  const user = auth?.user;

  return (
    <>
      {user?.role === "MANAGER" && <Navbar />}
      {user?.role === "STAFF" && <StaffNavbar />}

      <main style={{ padding: 20 }}>
        <Outlet />
      </main>
    </>
  );
}

export default MainLayout;
