import { useState } from "react";
import { createOnlineOrder } from "../../api/orderApi";

function OnlineOrderCreate() {
  const [customerId, setCustomerId] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [courses, setCourses] = useState({});

  const submit = async () => {
    const orderId = await createOnlineOrder(
      customerId ? Number(customerId) : null,
      address,
      phone,
      courses
    );

    alert("Online order created. Order ID: " + orderId);
  };

  return (
    <div>
      <h2>Create Online Order</h2>

      <input
        placeholder="Customer ID (optional)"
        value={customerId}
        onChange={e => setCustomerId(e.target.value)}
      /><br /><br />

      <input
        placeholder="Address"
        value={address}
        onChange={e => setAddress(e.target.value)}
      /><br /><br />

      <input
        placeholder="Phone"
        value={phone}
        onChange={e => setPhone(e.target.value)}
      /><br /><br />

      <textarea
        placeholder='Courses JSON e.g. {"Burger":2,"Pizza":1}'
        onChange={e => setCourses(JSON.parse(e.target.value))}
      />

      <br /><br />
      <button onClick={submit}>Create</button>
    </div>
  );
}

export default OnlineOrderCreate;
