import { useEffect, useState } from "react";
import { getOrderById, getOrderCourses, removeOrderCourse } from "../../api/orderApi";

function OrderDetail() {
  const [orderId, setOrderId] = useState("");
  const [order, setOrder] = useState(null);
  const [courses, setCourses] = useState([]);

  const load = async () => {
    const o = await getOrderById(orderId);
    const c = await getOrderCourses(orderId);
    setOrder(o);
    setCourses(c);
  };

  const remove = async (courseName) => {
    await removeOrderCourse(orderId, courseName);
    load();
  };

  return (
    <div>
      <h2>Order Detail</h2>

      <input
        placeholder="Order ID"
        value={orderId}
        onChange={e => setOrderId(e.target.value)}
      />
      <button onClick={load}>Load</button>

      {order && (
        <>
          <p><b>Date:</b> {order.date}</p>
          <p><b>Time:</b> {order.time}</p>
          <p><b>Total:</b> {order.orderPrice} â‚º</p>

          <h3>Courses</h3>

          <table border="1" cellPadding="8">
            <thead>
              <tr>
                <th>Course</th>
                <th>Quantity</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {courses.map(c => (
                <tr key={c.courseName}>
                  <td>{c.courseName}</td>
                  <td>{c.quantity}</td>
                  <td>
                    <button onClick={() => remove(c.courseName)}>Remove</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default OrderDetail;
