import { useEffect, useState } from "react";
import { getOrderById } from "../../api/orderApi";

function AllOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadAllOrders = async () => {
    setLoading(true);
    const results = [];

    let id = 1;

    while (true) {
      try {
        const order = await getOrderById(id);
        if (order) {
          results.push(order);
          id++;
        } else {
          break;
        }
      } catch (err) {
        // 404 gelince dur
        break;
      }
    }

    setOrders(results);
    setLoading(false);
  };

  useEffect(() => {
    loadAllOrders();
  }, []);

  return (
    <div>
      <h2>All Orders</h2>

      {loading && <p>Loading...</p>}

      {!loading && orders.length === 0 && (
        <p>No orders found.</p>
      )}

      {orders.length > 0 && (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>ID</th>
              <th>Status</th>
              <th>Price</th>
              <th>Date</th>
              <th>Time</th>
              <th>Deliverer</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AllOrders;
