import { useEffect, useState } from "react";
import { createOnlineOrder, getOrderById } from "../../api/orderApi";
import { getAllCourses } from "../../api/inventoryApi";

function OrderCreateOnline() {
  /* ================= DATA ================= */
  const [coursesList, setCoursesList] = useState([]);

  /* ================= FORM ================= */
  const [onlineCustomerId, setOnlineCustomerId] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= ORDERS ================= */
  const [onlineOrders, setOnlineOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  /* ================= INIT ================= */
  useEffect(() => {
    getAllCourses().then(setCoursesList);
    loadOnlineOrders();
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || quantity <= 0) return;

    setCourses(prev => ({
      ...prev,
      [selectedCourse]: (prev[selectedCourse] || 0) + Number(quantity)
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  /* ================= CREATE ORDER ================= */
  const submit = async () => {
    if (!address || !phone || Object.keys(courses).length === 0) {
      alert("Please fill address, phone and courses");
      return;
    }

    try {
      console.log("ðŸ“¤ ONLINE ORDER PAYLOAD:", {
        onlineCustomerId: onlineCustomerId ? Number(onlineCustomerId) : null,
        address,
        phone,
        courses
      });

      const orderId = await createOnlineOrder(
        onlineCustomerId ? Number(onlineCustomerId) : null,
        address,
        phone,
        courses
      );

      alert("Online order created (ID: " + orderId + ")");

      setOnlineCustomerId("");
      setAddress("");
      setPhone("");
      setCourses({});
      loadOnlineOrders();
    } catch (err) {
      console.error("âŒ ONLINE ORDER ERROR:", err);
      alert("Failed to create online order");
    }
  };

  /* ================= LOAD ONLINE ORDERS ================= */
  const loadOnlineOrders = async () => {
    setLoading(true);
    const results = [];

    for (let id = 1; id <= 300; id++) {
      try {
        const order = await getOrderById(id);

        // online order = tableNo YOK
        if (order && order.tableNo == null) {
          results.push(order);
        }
      } catch {}
    }

    setOnlineOrders(results);
    setLoading(false);
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 900 }}>
      <h2>ðŸšš Create Online Order</h2>

      {/* ===== FORM ===== */}
      <div style={{ border: "1px solid #ddd", padding: 16, marginBottom: 20 }}>
        <label>Customer ID (optional)</label><br />
        <input
          value={onlineCustomerId}
          onChange={e => setOnlineCustomerId(e.target.value)}
          placeholder="Leave empty for guest"
        />

        <br /><br />

        <label>Address</label><br />
        <input
          value={address}
          onChange={e => setAddress(e.target.value)}
          style={{ width: "100%" }}
        />

        <br /><br />

        <label>Phone</label><br />
        <input
          value={phone}
          onChange={e => setPhone(e.target.value)}
        />

        <br /><br />

        <label>Add Course</label><br />
        <select
          value={selectedCourse}
          onChange={e => setSelectedCourse(e.target.value)}
        >
          <option value="">Select Course</option>
          {coursesList.map(c => (
            <option key={c.courseName} value={c.courseName}>
              {c.courseName}
            </option>
          ))}
        </select>

        <input
          type="number"
          min="1"
          value={quantity}
          onChange={e => setQuantity(e.target.value)}
          style={{ width: 60, marginLeft: 6 }}
        />

        <button onClick={addCourse} style={{ marginLeft: 6 }}>
          Add
        </button>

        {/* COURSE LIST */}
        {Object.keys(courses).length > 0 && (
          <ul>
            {Object.entries(courses).map(([name, qty]) => (
              <li key={name}>
                {name} Ã— {qty}
              </li>
            ))}
          </ul>
        )}

        <button onClick={submit}>Create Online Order</button>
      </div>

      {/* ===== ORDERS ===== */}
      <h3>ðŸ“¦ Online Orders</h3>

      {loading && <p>Loadingâ€¦</p>}

      {!loading && onlineOrders.length === 0 && (
        <p>No online orders yet.</p>
      )}

      {onlineOrders.length > 0 && (
        <table border="1" cellPadding="8" width="100%">
          <thead>
            <tr>
              <th>ID</th>
              <th>Status</th>
              <th>Total</th>
              <th>Date</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {onlineOrders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default OrderCreateOnline;
