import { useState } from "react";
import { getOrderById } from "../../api/orderApi";

function AllOrders() {
  const [ids, setIds] = useState("");
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState("");

  const load = async () => {
    setOrders([]);
    setError("");

    const list = ids
      .split(",")
      .map(i => i.trim())
      .filter(Boolean);

    const result = [];

    for (const id of list) {
      try {
        const order = await getOrderById(id);
        result.push(order);
      } catch {
        setError("Some orders could not be loaded");
      }
    }

    setOrders(result);
  };

  return (
    <div>
      <h2>All Orders</h2>

      <input
        placeholder="1,2,3"
        value={ids}
        onChange={e => setIds(e.target.value)}
      />
      <button onClick={load}>Load</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {orders.length > 0 && (
        <table border="1" cellPadding="6">
          <thead>
            <tr>
              <th>ID</th>
              <th>Status</th>
              <th>Price</th>
              <th>Date</th>
              <th>Time</th>
              <th>Deliverer</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice}</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AllOrders;
