import { useState } from "react";
import { getScheduleByUserId } from "../../api/peopleApi";

function ScheduleView() {
  const [userId, setUserId] = useState("");
  const [slots, setSlots] = useState([]);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    setLoading(true);
    console.log("ðŸ“¡ Fetching schedule for userId:", userId);

    const data = await getScheduleByUserId(userId);
    console.log("ðŸ“¥ Schedule response:", data);

    setSlots(Array.isArray(data) ? data : []);
    setLoading(false);
  };

  return (
    <div>
      <h2>Employee Schedule</h2>

      <input
        placeholder="User ID"
        value={userId}
        onChange={e => setUserId(e.target.value)}
      />

      <button onClick={load}>
        {loading ? "Loading..." : "Load"}
      </button>

      {slots.length === 0 && !loading && <p>No schedule found</p>}

      <ul>
        {slots.map((s, i) => (
          <li key={i}>
            Day {s.dayOfWeek} â€” {s.startTime} â†’ {s.endTime}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ScheduleView;
