import { useState } from "react";
import { createOnlineOrder } from "../../api/orderApi";

function OrderCreateOnline() {
  const [customerId, setCustomerId] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [courses, setCourses] = useState({});
  const [courseName, setCourseName] = useState("");
  const [quantity, setQuantity] = useState(1);

  const addCourse = () => {
    setCourses(prev => ({
      ...prev,
      [courseName]: Number(quantity)
    }));
    setCourseName("");
    setQuantity(1);
  };

  const submit = async () => {
    const orderId = await createOnlineOrder(
      customerId ? Number(customerId) : null,
      address,
      phone,
      courses
    );
    alert("Online order created: " + orderId);
  };

  return (
    <div>
      <h2>Create Online Order</h2>

      <input placeholder="Customer ID (optional)"
        value={customerId}
        onChange={e => setCustomerId(e.target.value)} /><br /><br />

      <input placeholder="Address"
        value={address}
        onChange={e => setAddress(e.target.value)} /><br /><br />

      <input placeholder="Phone"
        value={phone}
        onChange={e => setPhone(e.target.value)} /><br /><br />

      <input placeholder="Course Name"
        value={courseName}
        onChange={e => setCourseName(e.target.value)} />

      <input type="number" min="1"
        value={quantity}
        onChange={e => setQuantity(e.target.value)} />

      <button onClick={addCourse}>Add Course</button>

      <pre>{JSON.stringify(courses, null, 2)}</pre>

      <button onClick={submit}>Create Online Order</button>
    </div>
  );
}

export default OrderCreateOnline;
