import { useState } from "react";
import { getScheduleByName } from "../../api/peopleApi";

const DAYS = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday"
];

function ScheduleView() {
  const [name, setName] = useState("");
  const [slots, setSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const loadSchedule = async () => {
    if (!name.trim()) {
      alert("Please enter employee full name");
      return;
    }

    setLoading(true);
    setError("");
    setSlots([]);

    try {
      console.log("游니 Loading schedule for:", name);
      const data = await getScheduleByName(name);
      console.log("游닌 Schedule response:", data);

      if (Array.isArray(data)) {
        setSlots(data);
      } else {
        setError("Invalid response from server");
      }
    } catch (err) {
      console.error(err);
      setError("Failed to load schedule");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Employee Schedule</h2>

      <input
        placeholder="Employee Full Name (EXACT)"
        value={name}
        onChange={e => setName(e.target.value)}
        style={{ marginRight: 10 }}
      />

      <button onClick={loadSchedule}>
        {loading ? "Loading..." : "Load Schedule"}
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && slots.length === 0 && !error && (
        <p style={{ marginTop: 20 }}>No schedule found.</p>
      )}

      {slots.length > 0 && (
        <table
          border="1"
          width="100%"
          style={{ marginTop: 20, borderCollapse: "collapse" }}
        >
          <thead>
            <tr>
              <th>Day</th>
              <th>Start Time</th>
              <th>End Time</th>
            </tr>
          </thead>
          <tbody>
            {slots.map((s, i) => (
              <tr key={i}>
                <td>{DAYS[s.dayOfWeek]}</td>
                <td>{s.startTime}</td>
                <td>{s.endTime}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ScheduleView;
