import React, { useEffect, useState } from "react";
import { getDailyDashboard } from "../../api/dashboardApi";

export default function ManagerDashboard() {
  const [data, setData] = useState(null);
  const [date, setDate] = useState("");
  const [error, setError] = useState("");

  const loadData = async () => {
    try {
      const result = await getDailyDashboard(date);
      setData(result);
      setError("");
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>ðŸ“Š Manager Dashboard</h2>

      <div style={{ marginBottom: "15px" }}>
        <label>Select Date: </label>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <button onClick={loadData} style={{ marginLeft: "10px" }}>
          Load
        </button>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {data && (
        <div style={{ display: "flex", gap: "20px" }}>
          <StatCard title="Orders" value={data.orderCount} />
          <StatCard title="Revenue (â‚º)" value={data.revenue} />
        </div>
      )}
    </div>
  );
}

function StatCard({ title, value }) {
  return (
    <div
      style={{
        border: "1px solid #ddd",
        borderRadius: "8px",
        padding: "20px",
        minWidth: "200px",
        textAlign: "center",
        background: "#f9f9f9",
      }}
    >
      <h3>{title}</h3>
      <p style={{ fontSize: "24px", fontWeight: "bold" }}>{value}</p>
    </div>
  );
}
