import React, { useState } from "react";
import { getItemUsageHistory } from "../../api/inventoryApi";

function ItemUsageHistory() {
  const [itemName, setItemName] = useState("");
  const [records, setRecords] = useState([]);

  const load = () => {
    getItemUsageHistory(itemName).then(setRecords);
  };

  return (
    <div>
      <h2>Item Usage History</h2>

      <input
        placeholder="Item Name"
        onChange={e => setItemName(e.target.value)}
      />
      <button onClick={load}>Load</button>

      <ul>
        {records.map((r, i) => (
          <li key={i}>
            {r.dateUsed} – {r.usageType} – {r.amountUsed}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ItemUsageHistory;
