import { useState } from "react";
import { createDineInOrder } from "../../api/orderApi";

function OrderCreateDineIn() {
  const [waiterId, setWaiterId] = useState("");
  const [tableNo, setTableNo] = useState("");
  const [courseName, setCourseName] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  const addCourse = () => {
    setCourses(prev => ({
      ...prev,
      [courseName]: Number(quantity)
    }));
    setCourseName("");
    setQuantity(1);
  };

  const submit = async () => {
    const orderId = await createDineInOrder(
      Number(waiterId),
      Number(tableNo),
      courses
    );
    alert("Order created: " + orderId);
  };

  return (
    <div>
      <h2>Create Dine-In Order</h2>

      <input placeholder="Waiter ID" value={waiterId}
        onChange={e => setWaiterId(e.target.value)} /><br /><br />

      <input placeholder="Table No" value={tableNo}
        onChange={e => setTableNo(e.target.value)} /><br /><br />

      <h4>Add Course</h4>
      <input placeholder="Course Name" value={courseName}
        onChange={e => setCourseName(e.target.value)} />

      <input type="number" min="1" value={quantity}
        onChange={e => setQuantity(e.target.value)} />

      <button onClick={addCourse}>Add</button>

      <pre>{JSON.stringify(courses, null, 2)}</pre>

      <button onClick={submit}>Create Order</button>
    </div>
  );
}

export default OrderCreateDineIn;
