import { useState } from "react";
import { getFullScheduleByUserId } from "../../api/peopleApi";

const DAYS = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday"
];

function ScheduleView() {
  const [userId, setUserId] = useState("");
  const [regular, setRegular] = useState([]);

  const load = async () => {
    if (!userId) return;

    try {
      const data = await getFullScheduleByUserId(userId);

      console.log("FULL SCHEDULE RESPONSE:", data);

      // üî• BACKEND ARRAY D√ñN√úYOR
      setRegular(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Schedule load error:", err);
      setRegular([]);
    }
  };

  return (
    <div style={{ maxWidth: 800 }}>
      <h2>üìÖ Employee Regular Schedule</h2>

      <input
        placeholder="User ID"
        value={userId}
        onChange={e => setUserId(e.target.value)}
      />
      <button onClick={load}>Load</button>

      <h3>üîÅ Regular Weekly Shifts</h3>

      <table border="1" width="100%" cellPadding="6">
        <thead>
          <tr>
            <th>Day</th>
            <th>Start</th>
            <th>End</th>
          </tr>
        </thead>
        <tbody>
          {regular.length === 0 && (
            <tr>
              <td colSpan="3" style={{ textAlign: "center" }}>
                No regular shifts
              </td>
            </tr>
          )}

          {regular.map((r, i) => (
            <tr key={i}>
              <td>{DAYS[r.dayOfWeek]}</td>
              <td>{r.startTime}</td>
              <td>{r.endTime}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ScheduleView;
