import { Outlet } from "react-router-dom";
import { useAuth } from "../pages/auth/AuthContext";
import Navbar from "./Navbar";
import StaffNavbar from "./StaffNavbar";

function MainLayout() {
  const auth = useAuth();

  // ðŸ”´ Ã‡OK Ã–NEMLÄ°: auth veya user yoksa SADECE outlet
  if (!auth || !auth.user) {
    return <Outlet />;
  }

  const { user } = auth;

  return (
    <>
      {user.role === "MANAGER" ? <Navbar /> : <StaffNavbar />}
      <Outlet />
    </>
  );
}

export default MainLayout;
