import React, { useEffect, useState } from "react";
import { updateItem, getAllItems } from "../../api/inventoryApi";
import { useParams, useNavigate } from "react-router-dom";

function ItemEdit() {
  const { name } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState(null);

  useEffect(() => {
    getAllItems().then(items => {
      const found = items.find(i => i.name === name);
      setForm(found);
    });
  }, [name]);

  if (!form) return <div>Loading...</div>;

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateItem(name, form).then(() => navigate("/items"));
  };

  return (
    <div>
      <h2>Edit Item: {name}</h2>

      <form onSubmit={handleSubmit}>
        <input value={form.price} name="price" type="number" step="0.01" onChange={handleChange} />
        <br />

        <input value={form.amount} name="amount" type="number" step="0.01" onChange={handleChange} />
        <br />

        <label>
          <input type="checkbox" name="containsMeat"
                 checked={form.containsMeat}
                 onChange={handleChange} />
          Contains Meat
        </label>
        <br />

        <label>
          <input type="checkbox" name="containsGluten"
                 checked={form.containsGluten}
                 onChange={handleChange} />
          Contains Gluten
        </label>
        <br /><br />

        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default ItemEdit;
