const BASE_URL = "/api/orders";

/* ======================
   ORDERS â€“ COMMON
====================== */


export const getAllOrders = async () => {
  console.warn("Orders API not implemented on backend yet.");
  return []; // ðŸ‘ˆ frontend patlamaz
};

export const getOrderById = async () => {
  return null;
};

export const createOrder = async () => {
  throw new Error("Orders API not implemented");
};
/*
courses = [
  { courseName, quantity }
]
*/
export const addCoursesToOrder = (orderId, courses) =>
  fetch(`${BASE_URL}/${orderId}/courses`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(courses)
  });

/* ======================
   TABLE ORDERS
====================== */

export const createTableOrder = (orderId, tableNo) =>
  fetch(`${BASE_URL}/${orderId}/table`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ tableNo })
  });

/* ======================
   ONLINE ORDERS
====================== */

export const createOnlineOrder = (orderId, address, phoneNumber) =>
  fetch(`${BASE_URL}/${orderId}/online`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ address, phoneNumber })
  });


  /* ======================
   ORDER COURSES
====================== */

export const getOrderCourses = (orderId) =>
  fetch(`/api/orders/${orderId}/courses`).then(res => res.json());

/*
courses = [
 { courseName: "Burger", quantity: 2 }
]
*/
export const addOrderCourses = (orderId, courses) =>
  fetch(`/api/orders/${orderId}/courses`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(courses)
  });

export const removeOrderCourse = (orderId, courseName) =>
  fetch(`/api/orders/${orderId}/courses/${courseName}`, {
    method: "DELETE"
  });


  /* ======================
   MANAGER REPORTS
====================== */

export const getMenuEfficiencyReport = (managerId, reportTitle) =>
  fetch(`/api/reports/${managerId}/${reportTitle}/menu`)
    .then(res => res.json());

export const getOrdersBetweenDates = (startDate, endDate) =>
  fetch(`/api/orders/range?start=${startDate}&end=${endDate}`)
    .then(res => res.json());
