const BASE_URL = "/api/orders";

/* ======================
   CREATE ORDERS
====================== */

export const createDineInOrder = (waiterId, tableNo, courses) =>
  fetch(`${BASE_URL}/dine-in`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ waiterId, tableNo, courses })
  }).then(res => res.json());

export const createOnlineOrder = (onlineCustomerId, address, phone, courses) =>
  fetch(`${BASE_URL}/online`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ onlineCustomerId, address, phone, courses })
  }).then(res => res.json());

/* ======================
   ORDER STATUS
====================== */

export const moveToPreparing = (orderId) =>
  fetch(`${BASE_URL}/${orderId}/preparing`, { method: "PATCH" });

export const moveToReady = (orderId) =>
  fetch(`${BASE_URL}/${orderId}/ready`, { method: "PATCH" });

export const serveOrder = (orderId, waiterId) =>
  fetch(`${BASE_URL}/${orderId}/serve/${waiterId}`, { method: "PATCH" });

export const claimOrder = (orderId, courierId) =>
  fetch(`${BASE_URL}/${orderId}/claim/${courierId}`, { method: "PATCH" });

export const deliverOrder = (orderId, courierId) =>
  fetch(`${BASE_URL}/${orderId}/deliver/${courierId}`, { method: "PATCH" });

/* ======================
   GETTERS
====================== */

export const getOrderById = (orderId) =>
  fetch(`${BASE_URL}/${orderId}`).then(res => res.json());

export const getReadyOnlineOrders = () =>
  fetch(`${BASE_URL}/ready-online`).then(res => res.json());
