import { useState, useEffect } from "react";
import { useAuth } from "../../auth/AuthContext";
import { useNavigate } from "react-router-dom";

function Login() {
  const [fullName, setFullName] = useState("");
  const [role, setRole] = useState("STAFF");

  const { login, user } = useAuth();
  const navigate = useNavigate();

  // Kullanıcı zaten login ise /login'e gelince geri yönlendir
  useEffect(() => {
    if (user) {
      if (user.role === "MANAGER") {
        navigate("/items", { replace: true });
      } else {
        navigate("/usage/create", { replace: true });
      }
    }
  }, [user, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();

    login(fullName, role);

    if (role === "MANAGER") {
      navigate("/items");
    } else {
      navigate("/usage/create");
    }
  };

  return (
    <div className="login-page">
      <div className="card">
        <div className="card-header">
          <h2 className="card-header-title">BilDine Login</h2>
          <p className="card-header-subtitle">
            Please enter your name and select your role.
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div>
            <label>Full Name</label>
            <input
              type="text"
              placeholder="Full Name"
              value={fullName}
              onChange={(e) => setFullName(e.target.value)}
              required
            />
          </div>

          <div>
            <label>Role</label>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="STAFF">Staff</option>
              <option value="MANAGER">Manager</option>
            </select>
          </div>

          <div className="mt-3">
            <button type="submit" className="w-full">
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
