const BASE_URL = "/orders";

/* ======================
   CREATE ORDERS
====================== */

export const createDineInOrder = async (waiterId, tableNo, courses) => {
  const res = await fetch(`${BASE_URL}/dine-in`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ waiterId, tableNo, courses })
  });

  if (!res.ok) throw new Error("Failed to create dine-in order");
  return res.json(); // orderId
};

export const createOnlineOrder = async (onlineCustomerId, address, phone, courses) => {
  const res = await fetch(`${BASE_URL}/online`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      onlineCustomerId,
      address,
      phone,
      courses
    })
  });

  if (!res.ok) throw new Error("Failed to create online order");
  return res.json(); // orderId
};

/* ======================
   GET ORDERS
====================== */

export const getOrderById = async (orderId) => {
  const res = await fetch(`${BASE_URL}/${orderId}`);
  if (res.status === 404) return null;
  if (!res.ok) throw new Error("Order fetch failed");
  return res.json();
};

export const getReadyOnlineOrders = async () => {
  const res = await fetch(`${BASE_URL}/ready-online`);
  if (!res.ok) throw new Error("Failed to fetch ready online orders");
  return res.json();
};

/* ======================
   ORDER ACTIONS
====================== */

export const moveToPreparing = (id) =>
  fetch(`${BASE_URL}/${id}/preparing`, { method: "PATCH" });

export const moveToReady = (id) =>
  fetch(`${BASE_URL}/${id}/ready`, { method: "PATCH" });

export const serveOrder = (id, waiterId) =>
  fetch(`${BASE_URL}/${id}/serve/${waiterId}`, { method: "PATCH" });

export const claimOrder = (id, courierId) =>
  fetch(`${BASE_URL}/${id}/claim/${courierId}`, { method: "PATCH" });

export const deliverOrder = (id, courierId) =>
  fetch(`${BASE_URL}/${id}/deliver/${courierId}`, { method: "PATCH" });
