import React, { useEffect, useState } from "react";
import { getCourseIngredients, addIngredient } from "../../api/inventoryApi";
import { useParams } from "react-router-dom";

function CourseIngredients() {
  const { courseName } = useParams();
  const [ingredients, setIngredients] = useState([]);
  const [itemName, setItemName] = useState("");
  const [amountRequired, setAmountRequired] = useState("");

  const loadIngredients = () => {
    getCourseIngredients(courseName).then(data => {
      console.log("ðŸ“¥ INGREDIENTS:", data);
      setIngredients(data);
    });
  };

  useEffect(() => {
    loadIngredients();
  }, [courseName]);

  const handleAdd = async (e) => {
    e.preventDefault();

    await addIngredient(courseName, {
      itemName,
      amountRequired: Number(amountRequired)
    });

    setItemName("");
    setAmountRequired("");
    loadIngredients();
  };

  return (
    <div>
      <h2>Ingredients for {courseName}</h2>

      <ul>
        {ingredients.map(pair => (
          <li key={pair.first.itemName}>
            {pair.first.itemName} â€“ required: {pair.second}
          </li>
        ))}
      </ul>

      <h3>Add Ingredient</h3>
      <form onSubmit={handleAdd}>
        <input
          placeholder="Item Name"
          value={itemName}
          onChange={e => setItemName(e.target.value)}
          required
        />
        <input
          type="number"
          step="0.01"
          placeholder="Amount Required"
          value={amountRequired}
          onChange={e => setAmountRequired(e.target.value)}
          required
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default CourseIngredients;
