import { useEffect, useState } from "react";
import { getAllUsers } from "../../api/peopleApi";
import { Link } from "react-router-dom";

function PeopleList() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadUsers = async () => {
    setLoading(true);
    try {
      const data = await getAllUsers();
      console.log("üì• USERS:", data);
      setUsers(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå Failed to load users:", err);
      setUsers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadUsers();
  }, []);

  return (
    <div>
      <h2>üë• People Management</h2>

      {/* GLOBAL ACTION BAR */}
      <div style={{ marginBottom: 20 }}>
        <Link to="/people/add">
          <button>‚ûï Add User</button>
        </Link>{" "}

        <Link to="/people/promote">
          <button>‚¨ÜÔ∏è Promote User</button>
        </Link>{" "}

        <Link to="/people/availability/requests">
          <button>‚úÖ Approve Requests</button>
        </Link>{" "}

        <Link to="/people/schedule">
          <button>üìÖ View Schedule</button>
        </Link>{" "}

        <Link to="/people/availability">
          <button>üïí New Availability</button>
        </Link>
      </div>

      {/* USERS TABLE */}
      <table border="1" width="100%" cellPadding="6">
        <thead style={{ background: "#f2f2f2" }}>
          <tr>
            <th>ID</th>
            <th>Full Name</th>
            <th>Salary / Hour</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {loading && (
            <tr>
              <td colSpan="4" align="center">Loading...</td>
            </tr>
          )}

          {!loading && users.length === 0 && (
            <tr>
              <td colSpan="4" align="center">No users found</td>
            </tr>
          )}

          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.fullName}</td>
              <td>{u.salaryPerHour}</td>
              <td>
                <Link to={`/people/schedule?userId=${u.id}`}>
                  <button>üìÖ Schedule</button>
                </Link>{" "}

                <Link to={`/people/promote?userId=${u.id}`}>
                  <button>‚¨ÜÔ∏è Promote</button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleList;
