import { useEffect, useState } from "react";
import { createOnlineOrder } from "../../api/orderApi";
import { getAllCourses } from "../../api/inventoryApi";

function OrderCreateOnline() {
  /* ================= DATA ================= */
  const [coursesList, setCoursesList] = useState([]);

  /* ================= FORM ================= */
  const [customerId, setCustomerId] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");

  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= INIT ================= */
  useEffect(() => {
    getAllCourses().then(setCoursesList);
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || quantity <= 0) return;

    setCourses(prev => ({
      ...prev,
      [selectedCourse]: quantity
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  /* ================= TOTAL PRICE ================= */
  const calculateTotalPrice = () => {
    let total = 0;

    for (const [name, qty] of Object.entries(courses)) {
      const course = coursesList.find(c => c.courseName === name);
      if (course) {
        total += course.price * qty;
      }
    }

    return total;
  };

  /* ================= SUBMIT ================= */
  const submit = async () => {
    if (!address || !phone || Object.keys(courses).length === 0) {
      alert("Please fill all required fields");
      return;
    }

    try {
      const orderId = await createOnlineOrder(
        customerId ? Number(customerId) : null,
        address,
        phone,
        courses
      );

      alert("Online order created (ID: " + orderId + ")");

      setCustomerId("");
      setAddress("");
      setPhone("");
      setCourses({});
    } catch (err) {
      console.error(err);
      alert("Failed to create online order");
    }
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 700 }}>
      <h2>ðŸ“¦ Create Online Order</h2>

      <div style={{ border: "1px solid #ddd", padding: 16 }}>
        {/* CUSTOMER */}
        <label>Customer ID (optional)</label><br />
        <input
          value={customerId}
          onChange={e => setCustomerId(e.target.value)}
        />

        <br /><br />

        {/* ADDRESS */}
        <label>Address *</label><br />
        <input
          value={address}
          onChange={e => setAddress(e.target.value)}
          style={{ width: "100%" }}
        />

        <br /><br />

        {/* PHONE */}
        <label>Phone *</label><br />
        <input
          value={phone}
          onChange={e => setPhone(e.target.value)}
        />

        <br /><br />

        {/* COURSE */}
        <label>Add Course</label><br />
        <select
          value={selectedCourse}
          onChange={e => setSelectedCourse(e.target.value)}
        >
          <option value="">Select Course</option>
          {coursesList.map(c => (
            <option key={c.courseName} value={c.courseName}>
              {c.courseName} ({c.price} â‚º)
            </option>
          ))}
        </select>

        <input
          type="number"
          min="1"
          value={quantity}
          onChange={e => setQuantity(e.target.value)}
          style={{ width: 60, marginLeft: 6 }}
        />

        <button onClick={addCourse} style={{ marginLeft: 6 }}>
          Add
        </button>

        {/* COURSE LIST */}
        {Object.keys(courses).length > 0 && (
          <>
            <h4>ðŸ§¾ Order Items</h4>
            <ul>
              {Object.entries(courses).map(([name, qty]) => (
                <li key={name}>
                  {name} Ã— {qty}
                </li>
              ))}
            </ul>

            <strong>Total: {calculateTotalPrice()} â‚º</strong>
          </>
        )}

        <br /><br />

        <button onClick={submit}>Create Online Order</button>
      </div>
    </div>
  );
}

export default OrderCreateOnline;
