import { useEffect, useState } from "react";
import {
  createOnlineOrder,
  getOrderById,
  moveToPreparing,
  moveToReady
} from "../../api/orderApi";
import { getAllCourses } from "../../api/inventoryApi";

const STATUS_LIST = ["RECEIVED", "PREPARING", "READY", "DELIVERED"];

function UserOnlineOrderCreate() {
  /* ================= DATA ================= */
  const [coursesList, setCoursesList] = useState([]);

  /* ================= CREATE FORM ================= */
  const [onlineCustomerId, setOnlineCustomerId] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= ORDERS ================= */
  const [onlineOrders, setOnlineOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  /* ================= EDIT ================= */
  const [editingId, setEditingId] = useState(null);
  const [editStatus, setEditStatus] = useState("");

  /* ================= INIT ================= */
  useEffect(() => {
    getAllCourses().then(setCoursesList);
    loadOnlineOrders();
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || Number(quantity) <= 0) return;

    setCourses((prev) => ({
      ...prev,
      [selectedCourse]: (prev[selectedCourse] || 0) + Number(quantity)
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  const removeCourse = (name) => {
    setCourses((prev) => {
      const copy = { ...prev };
      delete copy[name];
      return copy;
    });
  };

  /* ================= CREATE ORDER ================= */
  const submit = async () => {
    if (!address || !phone || Object.keys(courses).length === 0) {
      alert("Address, phone and courses required");
      return;
    }

    await createOnlineOrder(
      onlineCustomerId ? Number(onlineCustomerId) : null,
      address,
      phone,
      courses
    );

    alert("Online order created ‚úÖ");

    setOnlineCustomerId("");
    setAddress("");
    setPhone("");
    setCourses({});
    loadOnlineOrders();
  };

  /* ================= LOAD ORDERS ================= */
  const loadOnlineOrders = async () => {
    setLoading(true);
    const results = [];

    // Backend getAll yok -> demo i√ßin id tarƒ±yoruz
    for (let id = 1; id <= 300; id++) {
      try {
        const order = await getOrderById(id);
        if (order && order.tableNo == null) results.push(order);
      } catch {}
    }

    setOnlineOrders(results);
    setLoading(false);
  };

  /* ================= HELPERS ================= */
  const renderCourses = (order) => {
    const map = order.courses || order.orderCourses || {};
    if (!map || Object.keys(map).length === 0) return "-";

    return Object.entries(map).map(([name, qty]) => (
      <div key={name}>
        {name} √ó {qty}
      </div>
    ));
  };

  const startEdit = (o) => {
    setEditingId(o.orderId);
    setEditStatus(o.status);
  };

  const cancelEdit = () => setEditingId(null);

  const saveEdit = async (o) => {
    try {
      if (editStatus !== o.status) {
        if (editStatus === "PREPARING") await moveToPreparing(o.orderId);
        if (editStatus === "READY") await moveToReady(o.orderId);
        // DELIVERED i√ßin courierId lazƒ±m -> burada yapmƒ±yoruz
      }

      alert("Order updated ‚úÖ");
      cancelEdit();
      loadOnlineOrders();
    } catch (err) {
      console.error(err);
      alert("Status update failed ‚ùå");
    }
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 1200 }}>
      <h2>üßë‚Äçüíª User ‚Äì Online Order</h2>

      {/* ===== CREATE FORM ===== */}
      <div style={{ border: "1px solid #ddd", padding: 16, marginBottom: 20 }}>
        <h4>Create Online Order</h4>

        <input
          placeholder="Customer ID (optional)"
          value={onlineCustomerId}
          onChange={(e) => setOnlineCustomerId(e.target.value)}
        />
        <br /><br />

        <input
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          style={{ width: "100%" }}
        />
        <br /><br />

        <input
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <br /><br />

        <select value={selectedCourse} onChange={(e) => setSelectedCourse(e.target.value)}>
          <option value="">Select Course</option>
          {coursesList.map((c) => (
            <option key={c.courseName} value={c.courseName}>
              {c.courseName}
            </option>
          ))}
        </select>

        <input
          type="number"
          min="1"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          style={{ width: 60, marginLeft: 6 }}
        />

        <button onClick={addCourse} style={{ marginLeft: 6 }}>Add</button>

        {Object.keys(courses).length > 0 && (
          <ul style={{ marginTop: 10 }}>
            {Object.entries(courses).map(([n, q]) => (
              <li key={n}>
                {n} √ó {q}{" "}
                <button onClick={() => removeCourse(n)}>remove</button>
              </li>
            ))}
          </ul>
        )}

        <button onClick={submit}>Create Order</button>
        <button onClick={loadOnlineOrders} style={{ marginLeft: 8 }}>
          Refresh List
        </button>
      </div>

      {/* ===== ORDERS TABLE ===== */}
      <h3>üì¶ Online Orders</h3>
      {loading && <p>Loading‚Ä¶</p>}

      <table border="1" cellPadding="8" width="100%">
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Courses</th>
            <th>Status</th>
            <th>Total</th>
            <th>Date</th>
            <th>Time</th>
            <th>Edit</th>
          </tr>
        </thead>

        <tbody>
          {onlineOrders.map((o) => (
            <tr key={o.orderId}>
              <td>{o.orderId}</td>
              <td>{o.onlineCustomerId ? `Customer #${o.onlineCustomerId}` : "Guest"}</td>
              <td>{o.address}</td>
              <td>{o.phone || o.phoneNumber || "-"}</td>
              <td>{renderCourses(o)}</td>

              <td>
                {editingId === o.orderId ? (
                  <select value={editStatus} onChange={(e) => setEditStatus(e.target.value)}>
                    {STATUS_LIST.map((s) => (
                      <option key={s} value={s}>{s}</option>
                    ))}
                  </select>
                ) : (
                  o.status
                )}
              </td>

              <td>{o.orderPrice} ‚Ç∫</td>
              <td>{o.date}</td>
              <td>{o.time}</td>

              <td>
                {editingId === o.orderId ? (
                  <>
                    <button onClick={() => saveEdit(o)}>Save</button>
                    <button onClick={cancelEdit}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => startEdit(o)}>Edit</button>
                )}
              </td>
            </tr>
          ))}

          {!loading && onlineOrders.length === 0 && (
            <tr>
              <td colSpan="10" style={{ textAlign: "center", color: "#666" }}>
                No online orders found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default UserOnlineOrderCreate;
