import { useEffect, useState } from "react";
import {
  createOnlineOrder,
  getOrderById,
  moveToPreparing,
  moveToReady
} from "../../api/orderApi";
import { getAllCourses } from "../../api/inventoryApi";

const STATUS_FLOW = ["RECEIVED", "PREPARING", "READY", "DELIVERED"];

function OrderCreateOnline() {
  /* ================= DATA ================= */
  const [coursesList, setCoursesList] = useState([]);

  /* ================= FORM ================= */
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= ORDERS ================= */
  const [onlineOrders, setOnlineOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  /* ================= EDIT ================= */
  const [editingOrderId, setEditingOrderId] = useState(null);
  const [editAddress, setEditAddress] = useState("");
  const [editPhone, setEditPhone] = useState("");
  const [editStatus, setEditStatus] = useState("");

  /* ================= INIT ================= */
  useEffect(() => {
    getAllCourses().then(setCoursesList);
    loadOnlineOrders();
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || quantity <= 0) return;

    setCourses(prev => ({
      ...prev,
      [selectedCourse]: (prev[selectedCourse] || 0) + Number(quantity)
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  /* ================= CREATE ORDER ================= */
  const submit = async () => {
    if (!address || !phone || Object.keys(courses).length === 0) {
      alert("Fill address, phone and courses");
      return;
    }

    await createOnlineOrder(null, address, phone, courses);
    setAddress("");
    setPhone("");
    setCourses({});
    loadOnlineOrders();
  };

  /* ================= LOAD ================= */
  const loadOnlineOrders = async () => {
    setLoading(true);
    const results = [];

    for (let id = 1; id <= 300; id++) {
      try {
        const order = await getOrderById(id);
        if (order && order.tableNo == null) results.push(order);
      } catch {}
    }

    setOnlineOrders(results);
    setLoading(false);
  };

  /* ================= EDIT ================= */
  const startEdit = (o) => {
    setEditingOrderId(o.orderId);
    setEditAddress(o.address || "");
    setEditPhone(o.phone || "");
    setEditStatus(o.status);
  };

  const cancelEdit = () => {
    setEditingOrderId(null);
  };

  const saveEdit = async (order) => {
    try {
      // STATUS FLOW
      if (editStatus !== order.status) {
        if (editStatus === "PREPARING") await moveToPreparing(order.orderId);
        if (editStatus === "READY") await moveToReady(order.orderId);
      }

      alert("Order updated");
      cancelEdit();
      loadOnlineOrders();
    } catch (err) {
      console.error(err);
      alert("Status update failed");
    }
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 1100 }}>
      <h2>ðŸšš Online Orders</h2>

      {loading && <p>Loadingâ€¦</p>}

      <table border="1" cellPadding="8" width="100%">
        <thead>
          <tr>
            <th>ID</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Status</th>
            <th>Total</th>
            <th>Edit</th>
          </tr>
        </thead>

        <tbody>
          {onlineOrders.map(o => (
            <tr key={o.orderId}>
              <td>{o.orderId}</td>

              <td>
                {editingOrderId === o.orderId ? (
                  <input value={editAddress} onChange={e => setEditAddress(e.target.value)} />
                ) : (
                  o.address
                )}
              </td>

              <td>
                {editingOrderId === o.orderId ? (
                  <input value={editPhone} onChange={e => setEditPhone(e.target.value)} />
                ) : (
                  o.phone
                )}
              </td>

              <td>
                {editingOrderId === o.orderId ? (
                  <select value={editStatus} onChange={e => setEditStatus(e.target.value)}>
                    {STATUS_FLOW.map(s => (
                      <option key={s} value={s}>{s}</option>
                    ))}
                  </select>
                ) : (
                  o.status
                )}
              </td>

              <td>{o.orderPrice} â‚º</td>

              <td>
                {editingOrderId === o.orderId ? (
                  <>
                    <button onClick={() => saveEdit(o)}>Save</button>
                    <button onClick={cancelEdit}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => startEdit(o)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OrderCreateOnline;
