const BASE_URL = "/api/people";

/* USERS */
export const addUser = (data) =>
  fetch(`${BASE_URL}/users`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data)
  });

export const getAllUsers = async () => {
  const res = await fetch(`${BASE_URL}/users`);
  return res.json();
};

export const updateSalary = (fullName, salaryPerHour) =>
  fetch(`${BASE_URL}/users/salary`, {
    method: "PATCH",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ fullName, salaryPerHour })
  });

/* PROMOTION */
export const promoteToStaff = (userId) =>
  fetch(`${BASE_URL}/promote/staff/${userId}`, { method: "POST" });

export const promoteToManager = (userId, level) =>
  fetch(`${BASE_URL}/promote/manager/${userId}`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ level })
  });

/* SCHEDULE */
export const getScheduleByName = (fullName) =>
  fetch(`${BASE_URL}/schedule/name/${fullName}`).then(res => res.json());

export const requestAvailability = (data) =>
  fetch(`${BASE_URL}/availability`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data)
  });

export const requestTimeOff = (data) =>
  fetch(`${BASE_URL}/timeoff`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data)
  });
