import { useState } from "react";
import { getScheduleByName } from "../../api/peopleApi";

function ScheduleView() {
  const [name, setName] = useState("");
  const [slots, setSlots] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const load = async () => {
    if (!name.trim()) {
      alert("Please enter employee name");
      return;
    }

    setLoading(true);
    setError("");
    setSlots([]);

    try {
      console.log("üì° Fetching schedule for:", name);

      const data = await getScheduleByName(name);

      console.log("üì• RAW SCHEDULE RESPONSE:", data);

      if (Array.isArray(data)) {
        setSlots(data);
        console.log("‚úÖ Schedule slots count:", data.length);
      } else {
        console.error("‚ùå Expected array, got:", data);
        setError("Unexpected response from server");
      }
    } catch (err) {
      console.error("‚ùå Failed to load schedule:", err);
      setError("Failed to load schedule");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Employee Schedule</h2>

      <input
        placeholder="Employee Full Name"
        value={name}
        onChange={e => setName(e.target.value)}
      />

      <button onClick={load} disabled={loading}>
        {loading ? "Loading..." : "Load"}
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && slots.length === 0 && !error && (
        <p>No schedule found</p>
      )}

      <ul>
        {slots.map((s, i) => (
          <li key={i}>
            <strong>Day {s.dayOfWeek}</strong> ‚Äî {s.startTime} ‚Üí {s.endTime}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ScheduleView;
