import { useEffect, useState } from "react";
import { getOrderById } from "../../api/orderApi";

function AllOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadAllOrders = async () => {
    setLoading(true);
    const results = [];

    let id = 1;

    while (true) {
      try {
        const order = await getOrderById(id);
        if (order) {
          results.push(order);
          id++;
        } else {
          break;
        }
      } catch (err) {
        // 404 -> artÄ±k order yok
        break;
      }
    }

    setOrders(results);
    setLoading(false);
  };

  useEffect(() => {
    loadAllOrders();
  }, []);

  const renderType = (o) => {
    if (o.tableNo != null) return "ðŸ½ï¸ Dine-In";
    return "ðŸšš Online";
  };

  const renderDetails = (o) => {
    if (o.tableNo != null) {
      return `Table ${o.tableNo}`;
    }
    return o.address || "-";
  };

  const renderCourses = (o) => {
    const map = o.courses || o.orderCourses;
    if (!map || Object.keys(map).length === 0) return "-";

    return Object.entries(map).map(([c, q]) => (
      <div key={c}>
        {c} Ã— {q}
      </div>
    ));
  };

  return (
    <div style={{ maxWidth: 1200 }}>
      <h2>ðŸ“‹ All Orders</h2>

      {loading && <p>Loading...</p>}

      {!loading && orders.length === 0 && (
        <p>No orders found.</p>
      )}

      {orders.length > 0 && (
        <table border="1" cellPadding="8" width="100%">
          <thead>
            <tr>
              <th>ID</th>
              <th>Type</th>
              <th>Details</th>
              <th>Courses</th>
              <th>Status</th>
              <th>Total</th>
              <th>Date</th>
              <th>Time</th>
              <th>Deliverer</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{renderType(o)}</td>
                <td>{renderDetails(o)}</td>
                <td>{renderCourses(o)}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId ?? "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AllOrders;
