import React, { useEffect, useState } from "react";
import { getAllCourses, updateCourse } from "../../api/inventoryApi";
import { useParams, useNavigate } from "react-router-dom";

function CourseEdit() {
  const { name } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);

  useEffect(() => {
    getAllCourses().then(list => {
      setCourse(list.find(c => c.courseName === name));
    });
  }, [name]);

  if (!course) return <div>Loading...</div>;

  const handleChange = (e) => {
    setCourse({ ...course, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateCourse(name, course).then(() => navigate("/courses"));
  };

  return (
    <div>
      <h2>Edit Course</h2>

      <form onSubmit={handleSubmit}>
        <input value={course.price} name="price" onChange={handleChange} />
        <br />
        <input value={course.type} name="type" onChange={handleChange} />
        <br /><br />
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default CourseEdit;
