import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext"; // âœ… AYNI KLASÃ–R

function Login() {
  const auth = useAuth();
  const navigate = useNavigate();

  if (!auth) return null; // ðŸ›¡ï¸ crash Ã¶nler

  const { login, user } = auth;

  const [fullName, setFullName] = useState("");
  const [userId, setUserId] = useState("");
  const [role, setRole] = useState("STAFF");

  useEffect(() => {
    if (user) {
      navigate(user.role === "MANAGER" ? "/dashboard" : "/staff", {
        replace: true
      });
    }
  }, [user, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();
    login(fullName, role, Number(userId));
  };

  return (
    <div style={{ minHeight: "100vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
      <form onSubmit={handleSubmit} style={{ width: 320 }}>
        <h2>BilDine Login</h2>

        <input
          placeholder="Full Name"
          value={fullName}
          onChange={e => setFullName(e.target.value)}
          required
        />

        <input
          type="number"
          placeholder="User ID"
          value={userId}
          onChange={e => setUserId(e.target.value)}
          required
        />

        <select value={role} onChange={e => setRole(e.target.value)}>
          <option value="STAFF">Staff</option>
          <option value="MANAGER">Manager</option>
        </select>

        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
