import React, { useEffect, useState } from "react";
import { getCourseIngredients, addIngredient } from "../../api/inventoryApi";
import { useParams } from "react-router-dom";

function CourseIngredients() {
  const { courseName } = useParams();
  const [ingredients, setIngredients] = useState([]);
  const [itemName, setItemName] = useState("");
  const [amountRequired, setAmountRequired] = useState("");

  const loadIngredients = () => {
    getCourseIngredients(courseName).then(data => {
      console.log("üì• INGREDIENTS:", data);
      setIngredients(data);
    });
  };

  useEffect(() => {
    loadIngredients();
  }, [courseName]);

  const handleAdd = async (e) => {
    e.preventDefault();

    await addIngredient(courseName, {
      itemName,
      amountRequired: Number(amountRequired)
    });

    setItemName("");
    setAmountRequired("");
    loadIngredients();
  };

  return (
    <div>
      <h2>Ingredients for {courseName}</h2>

      {/* üîπ INGREDIENT LIST */}
      <table border="1" width="100%">
        <thead>
          <tr>
            <th>Item Name</th>
            <th>Required</th>
            <th>In Stock</th>
            <th>Cost</th>
            <th>Meat</th>
            <th>Gluten</th>
          </tr>
        </thead>

        <tbody>
          {ingredients.length === 0 && (
            <tr>
              <td colSpan="6">No ingredients added</td>
            </tr>
          )}

          {ingredients.map(pair => (
            <tr key={pair.first.itemName}>
              <td>{pair.first.itemName}</td>
              <td>{pair.second}</td>
              <td>{pair.first.amount}</td>
              <td>{pair.first.cost}</td>
              <td>{pair.first.containsMeat ? "ü•© Yes" : "‚ùå No"}</td>
              <td>{pair.first.containsGluten ? "üåæ Yes" : "‚ùå No"}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <br />

      {/* üîπ ADD INGREDIENT */}
      <h3>Add Ingredient</h3>
      <form onSubmit={handleAdd}>
        <input
          placeholder="Item Name"
          value={itemName}
          onChange={e => setItemName(e.target.value)}
          required
        />
        <input
          type="number"
          step="0.01"
          placeholder="Amount Required"
          value={amountRequired}
          onChange={e => setAmountRequired(e.target.value)}
          required
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default CourseIngredients;
