import { useEffect, useState } from "react";
import { createDineInOrder, getOrderById } from "../../api/orderApi";
import { getAllUsers } from "../../api/peopleApi";
import { getAllCourses } from "../../api/inventoryApi";

function OrderCreateDineIn() {
  /* ================= DATA ================= */
  const [users, setUsers] = useState([]);
  const [coursesList, setCoursesList] = useState([]);

  /* ================= FORM ================= */
  const [waiterId, setWaiterId] = useState("");
  const [tableNo, setTableNo] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= ORDERS ================= */
  const [dineInOrders, setDineInOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  /* ================= INIT ================= */
  useEffect(() => {
    getAllUsers().then(setUsers);
    getAllCourses().then(setCoursesList);
    loadDineInOrders();
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || quantity <= 0) return;

    setCourses(prev => ({
      ...prev,
      [selectedCourse]: Number(quantity)
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  /* ================= CREATE ORDER ================= */
const submit = async () => {
  console.log("ðŸŸ¡ SUBMIT CLICKED");

  console.log("ðŸ‘¨â€ðŸ³ waiterId:", waiterId);
  console.log("ðŸª‘ tableNo:", tableNo);
  console.log("ðŸ” courses:", courses);

  if (!waiterId || !tableNo || Object.keys(courses).length === 0) {
    alert("Fill all fields");
    return;
  }

  try {
    const orderId = await createDineInOrder(
      Number(waiterId),
      Number(tableNo),
      courses
    );

    console.log("âœ… ORDER CREATED ID:", orderId);
    alert("Order created: " + orderId);

    setCourses({});
    setTableNo("");
    loadDineInOrders();

  } catch (err) {
    console.error("âŒ CREATE ORDER ERROR:", err);
    alert("Failed to create dine-in order");
  }
};

  /* ================= LOAD DINE-IN ORDERS ================= */
  const loadDineInOrders = async () => {
    setLoading(true);
    const results = [];

    // backendâ€™de GET /orders yok â†’ ID Ã¼zerinden tarÄ±yoruz
    for (let id = 1; id <= 300; id++) {
      try {
        const order = await getOrderById(id);

        // dine-in order = tableNo olan
        if (order && order.tableNo !== undefined && order.tableNo !== null) {
          results.push(order);
        }
      } catch {
        // 404 â†’ geÃ§
      }
    }

    setDineInOrders(results);
    setLoading(false);
  };

  /* ================= UI ================= */
  return (
    <div>
      <h2>Create Dine-In Order</h2>

      {/* WAITER */}
      <select value={waiterId} onChange={e => setWaiterId(e.target.value)}>
        <option value="">Select Waiter</option>
        {users.map(u => (
          <option key={u.id} value={u.id}>
            {u.fullName} (ID: {u.id})
          </option>
        ))}
      </select>

      <br /><br />

      {/* TABLE */}
      <input
        type="number"
        min="1"
        placeholder="Table No"
        value={tableNo}
        onChange={e => setTableNo(e.target.value)}
      />

      <br /><br />

      {/* COURSE */}
      <select
        value={selectedCourse}
        onChange={e => setSelectedCourse(e.target.value)}
      >
        <option value="">Select Course</option>
        {coursesList.map(c => (
          <option key={c.courseName} value={c.courseName}>
            {c.courseName}
          </option>
        ))}
      </select>

      <input
        type="number"
        min="1"
        value={quantity}
        onChange={e => setQuantity(e.target.value)}
      />

      <button onClick={addCourse}>Add Course</button>

      <pre>{JSON.stringify(courses, null, 2)}</pre>

      <button onClick={submit}>Create Order</button>

      <hr />

      <h3>Dine-In Orders</h3>

      {loading && <p>Loading...</p>}

      {!loading && dineInOrders.length === 0 && (
        <p>No dine-in orders.</p>
      )}

      {dineInOrders.length > 0 && (
        <table border="1" cellPadding="8">
          <thead>
            <tr>
              <th>ID</th>
              <th>Table</th>
              <th>Status</th>
              <th>Price</th>
              <th>Date</th>
              <th>Time</th>
              <th>Waiter</th>
            </tr>
          </thead>
          <tbody>
            {dineInOrders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.tableNo}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} â‚º</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default OrderCreateDineIn;
