import { useEffect, useState } from "react";
import { getAllUsers } from "../../api/peopleApi";
import { Link } from "react-router-dom";

function PeopleList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    getAllUsers().then(setUsers);
  }, []);

  return (
    <div>
      <h2>üë• People Management</h2>

      {/* ACTION BAR */}
      <div style={{ marginBottom: 20 }}>
        <Link to="/people/add">
          <button>‚ûï Add User</button>
        </Link>{" "}

        <Link to="/people/promote">
          <button>‚¨ÜÔ∏è Promote User</button>
        </Link>{" "}

        {/* üî• REQUEST CREATE */}
        <Link to="/people/availability">
          <button>üïí Create Availability / Time-Off</button>
        </Link>{" "}

        {/* üî• MANAGER ONLY */}
        <Link to="/people/availability/requests">
          <button>üì• View Requests</button>
        </Link>{" "}

        <Link to="/people/schedule">
          <button>üìÖ Schedule</button>
        </Link>
      </div>

      {/* USERS TABLE */}
      <table border="1" width="100%" cellPadding="6">
        <thead style={{ background: "#f2f2f2" }}>
          <tr>
            <th>ID</th>
            <th>Full Name</th>
            <th>Salary / Hour</th>
            <th>Quick Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(u => (
            <tr key={u.id}>
              <td>{u.id}</td>
              <td>{u.fullName}</td>
              <td>{u.salaryPerHour}</td>
              <td>
                <Link to={`/people/schedule?userId=${u.id}`}>
                  <button>üìÖ Schedule</button>
                </Link>{" "}
                <Link to={`/people/promote?userId=${u.id}`}>
                  <button>‚¨ÜÔ∏è Promote</button>
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PeopleList;
