import React, { useEffect, useState } from "react";
import { getAllCourses, getCourseIngredients } from "../../api/inventoryApi";
import { Link, useLocation } from "react-router-dom";

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [openCourse, setOpenCourse] = useState(null);
  const [ingredientsMap, setIngredientsMap] = useState({});
  const location = useLocation();

  useEffect(() => {
    getAllCourses().then(data => {
      console.log("ðŸ“¥ COURSES FETCHED:", data);
      setCourses(data);
    });
  }, [location.pathname]);

  const toggleIngredients = async (courseName) => {
    // kapat
    if (openCourse === courseName) {
      setOpenCourse(null);
      return;
    }

    setOpenCourse(courseName);

    // daha Ã¶nce Ã§ekilmediyse backendâ€™den al
    if (!ingredientsMap[courseName]) {
      const data = await getCourseIngredients(courseName);
      console.log(`ðŸ“¥ INGREDIENTS for ${courseName}:`, data);

      setIngredientsMap(prev => ({
        ...prev,
        [courseName]: data
      }));
    }
  };

  return (
    <div>
      <h2>Courses</h2>

      <Link to="/courses/create">âž• Add Course</Link>
      <br /><br />

      <table border="1" width="100%">
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Type</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {courses.map(course => (
            <React.Fragment key={course.courseName}>
              <tr>
                <td>{course.courseName}</td>
                <td>{course.price}</td>
                <td>{course.type}</td>
                <td>
                  <Link to={`/courses/edit/${course.courseName}`}>Edit</Link>{" "}
                  <button
                    onClick={() => toggleIngredients(course.courseName)}
                    style={{ marginLeft: 8 }}
                  >
                    {openCourse === course.courseName
                      ? "Hide Ingredients"
                      : "Show Ingredients"}
                  </button>{" "}
                  <Link to={`/courses/${course.courseName}/availability`}>
                    Availability
                  </Link>
                </td>
              </tr>

              {openCourse === course.courseName && (
                <tr>
                  <td colSpan="4">
                    <strong>Ingredients:</strong>
                    <ul>
                      {(ingredientsMap[course.courseName] || []).length === 0 && (
                        <li>No ingredients</li>
                      )}

                      {(ingredientsMap[course.courseName] || []).map(pair => (
                        <li key={pair.first.itemName}>
                          {pair.first.itemName} â€“ required: {pair.second}
                        </li>
                      ))}
                    </ul>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CourseList;
