import { useState } from "react";
import { addOrderCourses } from "../../api/orderApi";

function OrderAddCourses() {
  const [orderId, setOrderId] = useState("");
  const [courseName, setCourseName] = useState("");
  const [quantity, setQuantity] = useState(1);

  const submit = async () => {
    await addOrderCourses(Number(orderId), [
      { courseName, quantity: Number(quantity) }
    ]);
    alert("Course added to order");
  };

  return (
    <div>
      <h2>Add Course to Order</h2>

      <input
        placeholder="Order ID"
        value={orderId}
        onChange={e => setOrderId(e.target.value)}
      /><br /><br />

      <input
        placeholder="Course Name"
        value={courseName}
        onChange={e => setCourseName(e.target.value)}
      /><br /><br />

      <input
        type="number"
        min="1"
        value={quantity}
        onChange={e => setQuantity(e.target.value)}
      /><br /><br />

      <button onClick={submit}>Add</button>
    </div>
  );
}

export default OrderAddCourses;
