import React, { useEffect, useState } from "react";
import { getCourseIngredients, addIngredient } from "../../api/inventoryApi";
import { useParams } from "react-router-dom";

function CourseIngredients() {
  const { courseName } = useParams();
  const [ingredients, setIngredients] = useState([]);
  const [itemName, setItemName] = useState("");
  const [amountRequired, setAmountRequired] = useState(0);

  const load = () => {
    getCourseIngredients(courseName).then(setIngredients);
  };

  useEffect(() => {
    load();
  }, [courseName]);

  const handleAdd = (e) => {
    e.preventDefault();
    addIngredient(courseName, { itemName, amountRequired })
      .then(load);
  };

  return (
    <div>
      <h2>Ingredients for {courseName}</h2>

      <ul>
        {ingredients.map(pair => (
          <li key={pair.first.name}>
            {pair.first.name} â€“ {pair.second}
          </li>
        ))}
      </ul>

      <h3>Add Ingredient</h3>
      <form onSubmit={handleAdd}>
        <input placeholder="Item Name" onChange={e => setItemName(e.target.value)} />
        <input type="number" step="0.01" onChange={e => setAmountRequired(e.target.value)} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CourseIngredients;
