import { useEffect, useState } from "react";

function UsageCreate() {
  const [items, setItems] = useState([]);
  const [itemName, setItemName] = useState("");
  const [usageType, setUsageType] = useState("USE");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetch("/api/inventory/items")
      .then(res => res.json())
      .then(data => {
        setItems(data);
        if (data.length > 0) setItemName(data[0].itemName);
      });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      itemName,
      usageType,
      amount: Number(amount)
    };
    if (date) payload.date = date;

    const res = await fetch("/api/inventory/usage", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    });

    const data = await res.json();

    setMessage(res.ok ? "✅ Usage recorded" : "❌ " + data.error);
    if (res.ok) {
      setAmount("");
      setDate("");
    }
  };

  return (
    <div>
      <h2>Record Usage</h2>

      <form onSubmit={handleSubmit}>
        <select value={itemName} onChange={e => setItemName(e.target.value)}>
          {items.map(i => (
            <option key={i.itemName} value={i.itemName}>
              {i.itemName}
            </option>
          ))}
        </select>

        <select value={usageType} onChange={e => setUsageType(e.target.value)}>
          <option value="USE">USE</option>
          <option value="WASTE">WASTE</option>
          <option value="RECEIVE">RECEIVE</option>
        </select>

        <input
          type="number"
          step="0.01"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          required
        />

        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
        />

        <button type="submit">Save</button>
      </form>

      {message && <p>{message}</p>}
    </div>
  );
}

export default UsageCreate;
