import React, { useState } from "react";
import { Link } from "react-router-dom";

function UsageList() {
  const [records, setRecords] = useState([]);
  const [type, setType] = useState("");
  const [loading, setLoading] = useState(false);

  const load = async () => {
    setLoading(true);

    // üîé LOG 1: current filter
    console.log("üü° LOAD CLICKED");
    console.log("üü° Selected type:", type);

    try {
      let url = "/api/inventory/usage";

      // üîé LOG 2: url building
      if (type) {
        url += `?type=${type}`;
      }

      console.log("üü° Final request URL:", url);

      const res = await fetch(url);

      // üîé LOG 3: response status
      console.log("üü° Response status:", res.status);

      const data = await res.json();

      // üîé LOG 4: raw response data
      console.log("üü¢ RAW RESPONSE DATA:", data);

      if (Array.isArray(data)) {
        console.log("‚úÖ Data is array, length:", data.length);
        setRecords(data);
      } else {
        console.error("‚ùå Expected array but got:", data);
        setRecords([]);
      }
    } catch (err) {
      console.error("‚ùå FETCH ERROR:", err);
      setRecords([]);
    } finally {
      setLoading(false);
      console.log("üü° LOAD FINISHED");
    }
  };

  return (
    <div>
      <h2>Usage Records</h2>

      {/* ACTION BAR */}
      <div style={{ marginBottom: 15 }}>
        <Link to="/usage/create">
          <button style={{ marginRight: 10 }}>‚ûï Add Usage</button>
        </Link>

        <select
          value={type}
          onChange={e => {
            console.log("üü° TYPE CHANGED:", e.target.value);
            setType(e.target.value);
          }}
          style={{ marginRight: 10 }}
        >
          <option value="">ALL</option>
          <option value="USE">USE</option>
          <option value="WASTE">WASTE</option>
          <option value="RECEIVE">RECEIVE</option>
        </select>

        <button onClick={load} disabled={loading}>
          {loading ? "Loading..." : "Load"}
        </button>
      </div>

      {/* TABLE */}
      <table border="1" width="100%">
        <thead>
          <tr>
            <th>Item</th>
            <th>Date</th>
            <th>Type</th>
            <th>Amount</th>
          </tr>
        </thead>

        <tbody>
          {!loading && records.length === 0 && (
            <tr>
              <td colSpan="4" style={{ textAlign: "center" }}>
                No records found
              </td>
            </tr>
          )}

          {records.map((r, i) => {
            console.log("üßæ Rendering record:", r);
            return (
              <tr key={`${r.itemName}-${r.dateUsed}-${r.usageType}-${i}`}>
                <td>{r.itemName}</td>
                <td>{r.dateUsed}</td>
                <td>{r.usageType}</td>
                <td>{r.amountUsed}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default UsageList;
