import Navbar from "./Navbar";          // Manager Navbar
import StaffNavbar from "./StaffNavbar";
import { Outlet } from "react-router-dom";
import { useAuth } from "../pages/auth/AuthContext";

function MainLayout() {
  const auth = useAuth();

  // ðŸ”´ Login ekranÄ±ndayken (user yokken) sadece sayfa render et
  if (!auth || !auth.user) {
    return (
      <main style={{ padding: 20 }}>
        <Outlet />
      </main>
    );
  }

  const { user } = auth;

  return (
    <>
      {/* ROLE BASED NAVBAR */}
      {user.role === "MANAGER" && <Navbar />}
      {user.role === "STAFF" && <StaffNavbar />}

      <main style={{ padding: 20 }}>
        <Outlet />
      </main>
    </>
  );
}

export default MainLayout;
