import React, { useState } from "react";
import { createItem } from "../../api/inventoryApi";
import { useNavigate } from "react-router-dom";

function ItemCreate() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    price: "",
    amount: "",
    containsMeat: false,
    containsGluten: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({
      ...form,
      [name]: type === "checkbox" ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const payload = {
      item_name: form.name,
      cost: Number(form.price),
      amount: Number(form.amount),
      contains_meat: form.containsMeat,
      contains_gluten: form.containsGluten
    };

    createItem(payload).then(() => navigate("/items"));
  };

  return (
    <div>
      <h2>Create Item</h2>

      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" onChange={handleChange} required />
        <br />

        <input name="price" type="number" step="0.01" placeholder="Price" onChange={handleChange} />
        <br />

        <input name="amount" type="number" step="0.01" placeholder="Amount" onChange={handleChange} />
        <br />

        <label>
          <input type="checkbox" name="containsMeat" onChange={handleChange} />
          Contains Meat
        </label>
        <br />

        <label>
          <input type="checkbox" name="containsGluten" onChange={handleChange} />
          Contains Gluten
        </label>

        <br /><br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default ItemCreate;
