import { useEffect, useState } from "react";
import { createDineInOrder, getOrderById } from "../../api/orderApi";
import { getAllUsers } from "../../api/peopleApi";
import { getAllCourses } from "../../api/inventoryApi";

function OrderCreateDineIn() {
  /* ================= DATA ================= */
  const [users, setUsers] = useState([]);
  const [coursesList, setCoursesList] = useState([]);

  /* ================= FORM ================= */
  const [waiterId, setWaiterId] = useState("");
  const [tableNo, setTableNo] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [courses, setCourses] = useState({});

  /* ================= ORDERS ================= */
  const [dineInOrders, setDineInOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  /* ================= INIT ================= */
  useEffect(() => {
    getAllUsers().then(setUsers);
    getAllCourses().then(setCoursesList);
    loadDineInOrders();
  }, []);

  /* ================= ADD COURSE ================= */
  const addCourse = () => {
    if (!selectedCourse || quantity <= 0) return;

    setCourses(prev => ({
      ...prev,
      [selectedCourse]: quantity
    }));

    setSelectedCourse("");
    setQuantity(1);
  };

  /* ================= CREATE ORDER ================= */
  const submit = async () => {
    if (!waiterId || !tableNo || Object.keys(courses).length === 0) {
      alert("Please fill all fields");
      return;
    }

    try {
      const orderId = await createDineInOrder(
        Number(waiterId),
        Number(tableNo),
        courses
      );

      alert("Order created (ID: " + orderId + ")");
      setCourses({});
      setTableNo("");
      loadDineInOrders();
    } catch (err) {
      console.error(err);
      alert("Failed to create order");
    }
  };

  /* ================= LOAD DINE-IN ORDERS ================= */
  const loadDineInOrders = async () => {
    setLoading(true);
    const results = [];

    for (let id = 1; id <= 300; id++) {
      try {
        const order = await getOrderById(id);
        if (order?.tableNo != null) {
          results.push(order);
        }
      } catch {}
    }

    setDineInOrders(results);
    setLoading(false);
  };

  /* ================= UI ================= */
  return (
    <div style={{ maxWidth: 900 }}>
      <h2>üçΩÔ∏è Create Dine-In Order</h2>

      {/* ===== FORM CARD ===== */}
      <div style={{ border: "1px solid #ddd", padding: 16, marginBottom: 20 }}>
        {/* WAITER */}
        <label>Waiter</label><br />
        <select value={waiterId} onChange={e => setWaiterId(e.target.value)}>
          <option value="">Select Waiter</option>
          {users
            .filter(u => u.delivererType === "waiter")
            .map(u => (
              <option key={u.id} value={u.id}>
                {u.fullName}
              </option>
            ))}
        </select>

        <br /><br />

        {/* TABLE */}
        <label>Table No</label><br />
        <input
          type="number"
          min="1"
          value={tableNo}
          onChange={e => setTableNo(e.target.value)}
        />

        <br /><br />

        {/* COURSE */}
        <label>Add Course</label><br />
        <select
          value={selectedCourse}
          onChange={e => setSelectedCourse(e.target.value)}
        >
          <option value="">Select Course</option>
          {coursesList.map(c => (
            <option key={c.courseName} value={c.courseName}>
              {c.courseName}
            </option>
          ))}
        </select>

        <input
          type="number"
          min="1"
          value={quantity}
          onChange={e => setQuantity(e.target.value)}
          style={{ width: 60, marginLeft: 6 }}
        />

        <button onClick={addCourse} style={{ marginLeft: 6 }}>
          Add
        </button>

        {/* COURSE LIST */}
        {Object.keys(courses).length > 0 && (
          <ul>
            {Object.entries(courses).map(([name, qty]) => (
              <li key={name}>
                {name} √ó {qty}
              </li>
            ))}
          </ul>
        )}

        <button onClick={submit}>Create Order</button>
      </div>

      {/* ===== ORDERS ===== */}
      <h3>üìã Dine-In Orders</h3>

      {loading && <p>Loading‚Ä¶</p>}

      {!loading && dineInOrders.length === 0 && (
        <p>No dine-in orders yet.</p>
      )}

      {dineInOrders.length > 0 && (
        <table border="1" cellPadding="8" width="100%">
          <thead>
            <tr>
              <th>ID</th>
              <th>Table</th>
              <th>Status</th>
              <th>Total</th>
              <th>Date</th>
              <th>Time</th>
              <th>Waiter ID</th>
            </tr>
          </thead>
          <tbody>
            {dineInOrders.map(o => (
              <tr key={o.orderId}>
                <td>{o.orderId}</td>
                <td>{o.tableNo}</td>
                <td>{o.status}</td>
                <td>{o.orderPrice} ‚Ç∫</td>
                <td>{o.date}</td>
                <td>{o.time}</td>
                <td>{o.delivererId}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default OrderCreateDineIn;
