import React, { useState } from "react";
import { createItem } from "../../api/inventoryApi";
import { useNavigate } from "react-router-dom";

function ItemCreate() {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    price: "",
    amount: "",
    containsMeat: false,
    containsGluten: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    setForm(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ðŸ”´ ekstra gÃ¼venlik
    if (!form.name.trim()) {
      alert("Item name is required");
      return;
    }

    const payload = {
      item_name: form.name.trim(),
      cost: Number(form.price),
      amount: Number(form.amount),
      contains_meat: form.containsMeat,
      contains_gluten: form.containsGluten
    };

    // ðŸ‘‡ DEBUG (Ã§ok Ã¶nemli)
    console.log("CREATE ITEM PAYLOAD:", payload);

    try {
      await createItem(payload);
      navigate("/items");
    } catch (err) {
      console.error("Create item failed:", err);
      alert("Item could not be created");
    }
  };

  return (
    <div>
      <h2>Create Item</h2>

      <form onSubmit={handleSubmit}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <br />

        <input
          name="price"
          type="number"
          step="0.01"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
          required
        />
        <br />

        <input
          name="amount"
          type="number"
          step="0.01"
          placeholder="Amount"
          value={form.amount}
          onChange={handleChange}
          required
        />
        <br />

        <label>
          <input
            type="checkbox"
            name="containsMeat"
            checked={form.containsMeat}
            onChange={handleChange}
          />
          Contains Meat
        </label>
        <br />

        <label>
          <input
            type="checkbox"
            name="containsGluten"
            checked={form.containsGluten}
            onChange={handleChange}
          />
          Contains Gluten
        </label>

        <br /><br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default ItemCreate;
