import React, { useEffect, useState } from "react";
import { getAllItems, deleteItem } from "../../api/inventoryApi";
import { Link } from "react-router-dom";

function ItemList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    getAllItems().then(setItems);
  }, []);
useEffect(() => {
  console.log("ITEMS FROM API:", items);
}, [items]);

  const handleDelete = (name) => {
    if (window.confirm("Are you sure?")) {
      deleteItem(name).then(() => {
        setItems(items.filter(i => i.item_name !== name));
      });
    }
  };

  return (
    <div>
      <h2>Items</h2>
      <Link to="/items/create">âž• Add Item</Link>

      <table border="1" width="100%">
        <thead>
          <tr>
            <th>Name</th>
            <th>Cost</th>
            <th>Amount</th>
            <th>Meat</th>
            <th>Gluten</th>
            <th>Actions</th>
          </tr>
        </thead>

<tbody>
  {items.map((item, index) => (
    <tr key={`${item.item_name}-${index}`}>
      <td>{item.item_name}</td>
      <td>{item.cost}</td>
      <td>{item.amount}</td>
      <td>{item.contains_meat ? "Yes" : "No"}</td>
      <td>{item.contains_gluten ? "Yes" : "No"}</td>
      <td>
        <Link to={`/items/edit/${item.item_name}`}>Edit</Link>{" "}
        <button onClick={() => handleDelete(item.item_name)}>
          Delete
        </button>
      </td>
    </tr>
  ))}
</tbody>

      </table>
    </div>
  );
}

export default ItemList;
