import { useEffect, useState } from "react";
import { getAvailabilityRequests, approveAvailability } from "../../api/peopleApi";

function AvailabilityRequestsList() {
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(false);

  const load = async () => {
    setLoading(true);
    const data = await getAvailabilityRequests();
    console.log("üì• AVAILABILITY REQUESTS:", data);
    setRequests(Array.isArray(data) ? data : []);
    setLoading(false);
  };

  useEffect(() => {
    load();
  }, []);

  const approve = async (req) => {
    await approveAvailability(req.userId, req.date, req.startTime);
    alert("Request approved");
    load();
  };

  return (
    <div>
      <h2>Availability Requests</h2>

      {loading && <p>Loading...</p>}

      <table border="1" width="100%">
        <thead>
          <tr>
            <th>User ID</th>
            <th>Date</th>
            <th>Start</th>
            <th>End</th>
            <th>Approved</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {requests.length === 0 && (
            <tr>
              <td colSpan="6" align="center">No requests</td>
            </tr>
          )}

          {requests.map((r, i) => (
            <tr key={i}>
              <td>{r.userId}</td>
              <td>{r.date}</td>
              <td>{r.startTime}</td>
              <td>{r.endTime}</td>
              <td>{r.approved ? "‚úÖ" : "‚ùå"}</td>
              <td>
                {!r.approved && (
                  <button onClick={() => approve(r)}>Approve</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AvailabilityRequestsList;
