import React, { useState } from "react";
import { createCourse } from "../../api/inventoryApi";
import { useNavigate } from "react-router-dom";

function CourseCreate() {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    price: 0,
    type: ""
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createCourse(form).then(() => navigate("/courses"));
  };

  return (
    <div>
      <h2>Create Course</h2>

      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Course Name" onChange={handleChange} required />
        <br />
        <input name="price" type="number" step="0.01" onChange={handleChange} />
        <br />
        <input name="type" placeholder="Type (Main, Dessert, Drink)" onChange={handleChange} />
        <br /><br />
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default CourseCreate;
